if(!RedactorPlugins)var RedactorPlugins={};RedactorPlugins.clips={init:function(){var i=$.proxy(function(){$("#redactor_modal").find(".redactor_clip_link").each($.proxy(function(i,t){$(t).click($.proxy(function(){return this.insertClip($(t).next().html()),!1},this))},this)),this.selectionSave(),this.bufferSet()},this);this.buttonAdd("clips","Clips",function(){this.modalInit("Clips","#clipsmodal",500,i)})},insertClip:function(i){this.selectionRestore(),this.insertHtml($.trim(i)),this.modalClose()}};